import { useEffect, useState } from "react";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Trophy, Star, Sparkles } from "lucide-react";
import confetti from "canvas-confetti";

interface BadgeUnlockModalProps {
  isOpen: boolean;
  onClose: () => void;
  badgeName: string;
  badgeDescription: string;
  badgeIcon?: any;
}

export function BadgeUnlockModal({
  isOpen,
  onClose,
  badgeName,
  badgeDescription,
  badgeIcon,
}: BadgeUnlockModalProps) {
  const [show, setShow] = useState(false);
  const BadgeIcon = badgeIcon || Trophy;

  useEffect(() => {
    if (isOpen) {
      setShow(true);
      triggerConfetti();

      const timer = setTimeout(() => {
        triggerConfetti();
      }, 300);

      return () => clearTimeout(timer);
    }
  }, [isOpen]);

  const triggerConfetti = () => {
    const count = 200;
    const defaults = {
      origin: { y: 0.7 },
      zIndex: 9999,
    };

    function fire(particleRatio: number, opts: any) {
      confetti({
        ...defaults,
        ...opts,
        particleCount: Math.floor(count * particleRatio),
      });
    }

    fire(0.25, {
      spread: 26,
      startVelocity: 55,
    });

    fire(0.2, {
      spread: 60,
    });

    fire(0.35, {
      spread: 100,
      decay: 0.91,
      scalar: 0.8,
    });

    fire(0.1, {
      spread: 120,
      startVelocity: 25,
      decay: 0.92,
      scalar: 1.2,
    });

    fire(0.1, {
      spread: 120,
      startVelocity: 45,
    });
  };

  const handleClose = () => {
    setShow(false);
    setTimeout(() => {
      onClose();
    }, 300);
